parameters.inputSchema:
{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "type": "object",
  "properties": {
    "name": {
      "type": "string",
      "description": "Name of the user"
    },
    "surname": {
      "type": "string",
      "description": "Surname of the user"
    },
    "commtype": {
      "type": "string",
      "enum": ["email", "phone", "other"],
      "description": "Method of communication"
    },
    "contacts": {
      "type": "string",
      "description": "Contact details. Required if commtype is 'email' or 'phone'"
    },
    "timestamp": {
      "type": "string",
      "format": "date-time",
      "description": "When the communication occurred",
      "pattern": "^(\\d{4}-[01]\\d-[0-3]\\d[T\\s][0-2]\\d:[0-5]\\d:[0-5]\\d(?:\\.\\d+)?(?:[+-][0-2]\\d:[0-5]\\d|Z)?)$"
    },
    "subject": {
      "type": "string",
      "description": "Brief description of the communication topic"
    }
  },
  "required": ["name", "commtype"],
  "if": {
    "properties": { "commtype": { "enum": ["email", "phone"] } }
  },
  "then": {
    "required": ["contacts"]
  }
}
