  const apiKey = "AIzaSyCinZYl58rnUTPpNX_xzqdAGnc6uPak-FE"; // Remplacer par ta clé API
  const cx = "f5db20188c61d409d";
  const query = "cars"; // Modifier la requête si besoin
 const url = `https://www.googleapis.com/customsearch/v1?key=${apiKey}&cx=${cx}&q=${encodeURIComponent(query)}`;

  // Options de la requête HTTP
  // Options de la requête HTTP
  const options = {
    method: 'GET',
    uri: url,
    json: true,
    simple: false // Permet de gérer manuellement les codes de statut HTTP
  };

  // Effectuer la requête HTTP avec this.helpers.request
  const response = await this.helpers.request(options);

  // Vérification du code de statut HTTP
  if (response.statusCode && response.statusCode >= 400) {
    // Gérer l'erreur en fonction du code de statut et du message
    throw new Error(`Request failed with status code ${response.statusCode}: ${response.body}`);
  }
