  // Vérification de la présence de la propriété 'items' et que c'est un tableau
  if (!response.items || !Array.isArray(response.items)) {
    throw new Error('La réponse de l\'API ne contient pas de propriété "items" valide.');
  }

  // Traitement de la réponse : extraction de l'attribut htmlTitle de chaque résultat
  const output = response.items.map(item => {
