async function createDriveFolder(name, parentId) {
  try {
    // 1. RÃ©cupÃ©ration directe des credentials
    const credentials = await this.getCredentials('Google Drive account'); // ðŸ”¼ Remplacez par votre nom de credentials

    // 2. VÃ©rification renforcÃ©e
    if (!credentials?.accessToken) {
      throw new Error('Credentials manquants ou invalides');
    }

    // 3. Recherche avec encodage URL
    const searchResponse = await this.$request({
      method: 'GET',
      uri: 'https://www.googleapis.com/drive/v3/files',
      qs: {
        q: `name='${encodeURIComponent(name)}' and '${parentId}' in parents and mimeType='application/vnd.google-apps.folder' and trashed=false`,
        fields: 'files(id,name)',
        pageSize: 1
      },
      auth: { bearer: credentials.accessToken }
    });

    if (searchResponse.files?.length > 0) {
      return searchResponse.files[0].id;
    }

    // 4. CrÃ©ation avec gestion des erreurs dÃ©taillÃ©e
    const createResponse = await this.$request({
      method: 'POST',
      uri: 'https://www.googleapis.com/drive/v3/files',
      body: {
        name,
        mimeType: 'application/vnd.google-apps.folder',
        parents: [parentId]
      },
      auth: { bearer: credentials.accessToken }
    });

    return createResponse.id;

  } catch (error) {
    console.error(`Erreur technique complÃ¨te : ${error.stack}`);
    throw new Error(`Ã‰chec crÃ©ation '${name}' : ${error.message}`);
  }
}

