const items = $input.all();
const mdFiles = [];
for (const item of items) {
  if (item.json.tree) {
    for (const file of item.json.tree) {
      if (file.path.endsWith('.md')) {
        mdFiles.push({ json: { path: file.path, url: file.url } });
      }
    }
  }
}
return mdFiles;

const items = $input.all();
for (const item of items) {
  const content = item.json.content;
  const buff = Buffer.from(content, 'base64');
  item.json.content = buff.toString('utf-8');
}
return items;
