async function createFolder(name, parentId) {
    const result = await $node['HTTP Request2'].execute({
        method: 'POST',
        url: 'https://www.googleapis.com/drive/v3/files',
        headers: {
            Authorization: `Bearer {{$credentials.googleDriveOAuth2Api.accessToken}}`,
            'Content-Type': 'application/json'
        },
        body: {
            name,
            mimeType: 'application/vnd.google-apps.folder',
            parents: [parentId]
        }
    });

    if (!result || !result.id) {
        throw new Error('La création du dossier a échoué.');
    }

    // ✅ Retourner sous forme de tableau d'objets JSON
    return [{ json: { id: result.id, name } }];
}

