const items = $input.all();
const folderSet = new Set();

for (const item of items) {
  const filePath = item.json.path;

  // On s'assure que le fichier est bien dans "docs/"
  if (filePath.startsWith('docs/')) {
    const parts = filePath.split('/');

    // Supprimer le nom du fichier (dernier élément) pour ne garder que les dossiers
    parts.pop();

    // On construit chaque sous-niveau et on ajoute dans un Set pour éviter les doublons
    let currentPath = '';
    for (const part of parts) {
      currentPath += (currentPath ? '/' : '') + part;
      folderSet.add(currentPath);
    }
  }
}

// Retourner tous les chemins de répertoires uniques sous forme d'items n8n
return Array.from(folderSet).map(path => ({ json: { path } }));

try {
  // ✅ Vérification de la présence des données d'entrée
  if (!$input || typeof $input.all !== 'function') {
    throw new Error("Aucune donnée d'entrée fournie !");
  }

  // ✅ Récupérer tous les éléments d'entrée
  const items = $input.all();

  // ✅ Vérification que les données sont bien sous forme de tableau
  if (!Array.isArray(items) || !items.length) {
    throw new Error("Les données d'entrée ne sont pas au format attendu !");
  }

  // ✅ Vérification de la présence du nœud 'setFields'
  if (!$node["setFields"] || !$node["setFields"].json) {
    throw new Error("Le nœud 'setFields' est manquant ou mal configuré !");
  }

  // ✅ Récupérer les extensions depuis le nœud 'setFields'
  let rawExtensions = $node["setFields"].json.extensions;

  // ✅ LOG POUR DEBUG → Vérifie ce qui est réellement présent dans le champ extensions
  console.log("➡️ Extensions trouvées dans le nœud setFields:", rawExtensions);

  // ✅ Si `rawExtensions` est null ou undefined → le remplacer par un tableau vide
  if (rawExtensions == null) {
    console.warn("⚠️ Le champ 'extensions' est null ou undefined → Conversion en tableau vide.");
    rawExtensions = [];
  }

  // ✅ Si `rawExtensions` est un objet, convertir uniquement les clés qui sont à `true`
  if (typeof rawExtensions === 'object' && !Array.isArray(rawExtensions)) {
    console.warn("⚠️ Le champ 'extensions' est un objet. Conversion en tableau basée sur les clés à `true`...");
    rawExtensions = Object.keys(rawExtensions).filter(key => rawExtensions[key] === true);
  }

  // ✅ Vérification que les extensions sont bien sous forme de tableau
  if (!Array.isArray(rawExtensions)) {
    throw new Error(`Le champ 'extensions' doit être un tableau. Type trouvé : ${typeof rawExtensions}`);
  }

  // ✅ Nettoyer le tableau en supprimant les valeurs vides ou non valides
  rawExtensions = rawExtensions
    .map(ext => typeof ext === 'string' ? ext.trim() : null) // Nettoyage des espaces
    .filter(ext => ext !== null && ext !== '');

  // ✅ LOG POUR DEBUG → Vérification après nettoyage
  console.log("➡️ Extensions après nettoyage :", rawExtensions);

  // ✅ Si le tableau est encore vide après nettoyage → Afficher une alerte (pas une erreur)
  if (!rawExtensions.length) {
    console.warn("⚠️ Le champ 'extensions' est vide après nettoyage → Vérifie la configuration !");
    throw new Error("Aucune extension valide trouvée dans le champ 'extensions' du nœud 'setFields' !");
  }

  // ✅ Conversion en format `.extension`
  const allowedExtensions = rawExtensions.map(ext => `.${ext.toLowerCase()}`);

  // ✅ LOG POUR DEBUG → Vérification du tableau final
  console.log("✅ Extensions autorisées après conversion :", allowedExtensions);

  // ✅ Récupérer le chemin du sous-répertoire depuis le nœud 'setFields'
  const subdirectoryPath = $node["setFields"].json['folderDocs'] || '';

  // ✅ Vérification du chemin de répertoire
  if (typeof subdirectoryPath !== 'string' || subdirectoryPath.trim() === '') {
    throw new Error("Chemin du sous-répertoire invalide !");
  }

  // ✅ Initialiser un tableau pour stocker les fichiers filtrés
  const filteredFiles = [];

  // ✅ Parcourir chaque élément d'entrée
  for (const item of items) {
    // Vérification de l'existence de l'arbre de fichiers
    if (item.json && Array.isArray(item.json.tree)) {
      for (const file of item.json.tree) {
        if (file.path && file.url) {
          // ✅ Extraire l'extension du fichier
          const fileExtension = `.${file.path.split('.').pop().toLowerCase()}`;

          // ✅ Vérifier si le fichier est dans le sous-répertoire spécifié
          if (
            file.path.startsWith(subdirectoryPath) &&
            allowedExtensions.includes(fileExtension)
          ) {
            filteredFiles.push({
              json: { path: file.path, url: file.url }
            });
          }
        }
      }
    }
  }

  // ✅ Vérification finale si aucun fichier n'a été trouvé
  if (!filteredFiles.length) {
    console.warn("⚠️ Aucun fichier correspondant n'a été trouvé !");
  }

  // ✅ Retourner les fichiers filtrés
  return filteredFiles;

} catch (error) {
  console.error(`❌ Erreur détectée : ${error.message}`);
  throw new Error(`Erreur : ${error.message}`);
}

